import { useState } from "react";
import "./css/App.css";
import "./css/all.css";
function App() {
  function inputFocusFunc(e) {
    if(e.target.value.length <= 0) e.target.nextElementSibling.classList.add("placeholder-trans")
  }
  function inputBlurFunc(e) {
    if(e.target.value.length <= 0) e.target.nextElementSibling.classList.add("placeholder-trans")
  }
  return (
    <div className="container">
      <div className="sign-container">
        <div className="sign-in">
          <div className="title">Sign up</div>
          <div className="information">
            <div className="input-container">
              <input onFocus={(e) => inputFocusFunc(e)} />
              <div className="placeholder">username</div>
            </div>
            <div className="input-container">
              <input onFocus={(e) => inputFocusFunc(e)} />
              <div className="placeholder">email</div>
            </div>
            <div className="input-container">
              <input onBlur={(e) => inputBlurFunc(e)} onFocus={(e) => inputFocusFunc(e)} />
              <div className="placeholder">password</div>
            </div>
          </div>
          <div className="agreements">
            <div className="checkbox">
              <i class="fa-solid fa-check"></i>
            </div>
            <div className="checkbox-info">
              I have read ....{" "}
              <span>
                I have read and accepted all <a href="#">terms</a> and{" "}
                <a href="#">conditions</a>
              </span>
            </div>
          </div>
          <div className="button-signup-container">
            <button className="signup-button">Sign up</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
