import { useState } from 'react';

function App() {

    let [input, setInput] = useState('');
    let [values, setValues] = useState([]);

    async function putNget() {

        setInput("")

        try {
                let f  = await fetch("http://localhost/react_proj/api.php", {
                method: "POST",
                headers: {"Content-type": "application/json"},
                body: JSON.stringify({"new_input": input})
            })

            let res = await f.json();

            setValues(res)

        } catch(e) {
            console.error(e);
        }
    }

return (
    <>
        <input onChange={(e) => setInput(e.target.value)} value={input} />
        <button onClick={putNget} >add</button>
        <ul>
            {values.map((e) => {
                return <li>id: {e.id}, name: {e.name}, password: {e.password}, role: {e.role}</li>
            })}
        </ul>
    </>
)
}

export default App;
