import { BrowserRouter, Routes, Route } from "react-router-dom";
import HomePage from "./components/home_comp/js/home.js";
import Sign from "./components/sign_comp/js/sign.js";
import Err from "./components/err_comp/js/index.js";
function App() {
  async function signUser(username, token) {
    try {
      const fet = await fetch(
        "http://localhost/verant_apis/token_checker.php",
        {
          method: "POST",
          headers: { "content-type": "application/json" },
          body: JSON.stringify({ username: username, token: token }),
        }
      );
      const res = await fet.json();
      if (res.message == "valid quick login") return true;
      else return false;
    } catch (e) {
      return false;
    }
  }

  function returnSign() {
    signUser(
      window.localStorage["username"],
      window.localStorage["token"]
    )


      if (signUser(
        window.localStorage["username"],
        window.localStorage["token"]
      )) {
        return <Route path="/" element={<HomePage />} />;
      } else {
        return <Route path="/" element={<Sign />} />;
      }
  }

  return (
    <>
      {
        <BrowserRouter>
          <Routes>
            {returnSign()}
            <Route path="*" element={<Err />} />
          </Routes>
        </BrowserRouter>
      }
    </>
  );
}

export default App;
