import { useState } from 'react';

function App() {

    let [input, setInput] = useState('');

    async function putNget() {
        fetch("http://localhost/react_proj/api.php", {
            method: "POST",
            headers: {"Content-type": "applicat"},
            body: {"new_input": input}
        })
    }

return (
    <>
        <input onChange={(e) => setInput(e.target.value)} />
        <button onClick={putNget} >add</button>
    </>
)
}

export default App;
