      <BrowserRouter>
        <Routes>
          {availableToken ? <Route path="/" element={<HomePage />} /> : <Route path="/" element={<Sign />} />}
          {availableToken ? <Route path="/shop" element={<ShopPage />} /> : <Route path="/shop" element={<Sign />} />}
          <Route path="*" element={<Err />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default App;
