import { useState } from "react";
import "../css/index.css";
import "../../../css/all.css";
export default function SideBar() {
    let [expand, setExpand] = useState(false);
    function expandSideBar(bool,e) {
        if (bool) {
            console.log("expanded")
        } else {
            console.log("decreased")

        }
    }
   return (
    <div>
        <div className="main-side-bar">
            <div className="expand-side-bar" onClick={(e) => {expandSideBar(expand); expand ? setExpand(false) : setExpand(true)}} >
                <div><i class="fa-solid fa-arrow-right"></i></div>
            </div>
            <div className="icons-container">
                <div className="icons-section">
                    <div className="sidebar-icon icon-active"><i class="fa-solid fa-house"></i></div>
                    <div className="sidebar-icon"><i class="fa-solid fa-gamepad"></i></div>
                    <div className="sidebar-icon"><i class="fa-solid fa-arrow-up-short-wide"></i></div>
                    <div className="sidebar-icon"><i class="fa-solid fa-rocket"></i></div>
                    <div className="sidebar-icon"><i class="fa-solid fa-ellipsis"></i></div>
                </div>
                <div className="icons-section">
                    <div className="sidebar-icon icon-settings"><i class="fa-solid fa-gear"></i></div>
                    </div>
            </div>
        </div>
    </div>
   )
}
