import {useRef} from "react";
import clickAudio from "../../click_audio_func/click_audio_func";
import hoverAudio from "../../hover_audio_func/hover_audio_func";
import "../css/side_bar.css";
export default function SideBar() {

  const catagorySlider = useRef(null);

  function changeCatagorySideBar(num) {
    if (num === 1) {
      catagorySlider.current.style.left = "0%";
    } else {
      catagorySlider.current.style.left = "50%";
    }
  }

  const overlay = {
    position: "absolute",
    width: "100%",
    height: "100%",
    left: "0",
    top: "0",

  };

  return (
    <div className="side-bar-main-container">
      <div className="menue-part-side-bar">
        <div className="menue-options-container-side-bar" onMouseOver={() => hoverAudio()} onClick={() => clickAudio()}>
          Report{" "}
          <span>
            <i class="fa-solid fa-circle-exclamation"></i>
          </span>
          <div style={overlay}></div>
        </div>
        <div className="menue-options-container-side-bar" onMouseOver={() => hoverAudio()} onClick={() => clickAudio()}>
          <i class="fa-solid fa-gear"></i>
          <div style={overlay}></div>
        </div>
      </div>
      <div className="side-bar-catagory-options-main-container">
        <div onClick={() => {changeCatagorySideBar(1); clickAudio()}} className="catagory-option catagory-option-acitve">Friends</div>
        <div onClick={() => {changeCatagorySideBar(2); clickAudio()}} className="catagory-option ">Requests</div>
        <div ref={catagorySlider} className="catagory-option-slider"></div>
      </div>
    </div>
  );
}
