import { useRef, useState } from "react";
import "../css/index.css";
import rankImg from "../../../ranks/ace/ace.png" 
export default function FloatingeBar() {
  function FloatingNaveRankCurrent() {
    return (
      <div className="floating-nav-content-container">
        <div className="floating-nav-content-rank-img">
          <img src={rankImg} alt="Rank" />
          <span className="rank-name-floating-nav">Ace</span>
        </div>
        <div className="floating-nav-content-rank-progress-container">
          <div className="curr-and-next-progg-rank-container">
            <div className="curr-progg-rank"><img src={rankImg} alt="curr-rank" /><span>ACE I</span></div>
            <div className="next-progg-rank"><img src={rankImg} alt="curr-rank" /><span>ACE II</span></div>
          </div>
          <div className="progg-bar">
            <span className="part1-prog-bar"></span>
            <span className="part2-prog-bar"></span>
            <span className="part3-prog-bar"></span>
          </div>
        </div>
        <div className="wins-and-losts-container">
          <div>
            <span>Wins</span>
            <span>12</span>
          </div>
          <div>
            <span>Losts</span>
            <span>6</span>
          </div>
        </div>
      </div>
    )
  }
  function FloatingNaveRankContainer() {
    return (
      <>
      <div className="floating-titles-container">
        <div className="a-floating-title-active a-floating-title">Current</div>
        <div className="a-floating-title">Ranks</div>
      </div>
      < FloatingNaveRankCurrent />
      </>
    )
  }

  const underLineFloating = useRef();
  const floatingNavOverlay = useRef();
  let [allowRemoveNavUnderLine,setAllowRemoveNavUnderLine ] = useState(true);
  function showFloatingNavContent(type) {
    if (type === "noti") {

    }
  }
  // for removing the blur overlay
  function removeFloatingNavOverlay(bool) {
    if (bool) {
      setAllowRemoveNavUnderLine(true);
      const left = underLineFloating.current.style.left;
      underLineFloating.current.style.cssText = `opacity: 0; left: ${left}`;
      floatingNavOverlay.current.style.cssText = "opacity: 0; display: block";
      setTimeout(() => floatingNavOverlay.current.style.cssText = "opacity: 0; display: none",400)
    } else {
      setAllowRemoveNavUnderLine(false);
      floatingNavOverlay.current.style.cssText = "display: block";
      setTimeout(() => floatingNavOverlay.current.style.cssText = "opacity: 1; display: block",0)
      // setTimeout(() => floatingNavOverlay.current.style.cssText = "opacity: 1; display: block",200)
    }
  }
  // for moving the underline for thefloating nav
  function MoveFloatingBarUnderLineLeave() {
    const left = underLineFloating.current.style.left;
    console.log("here")
    if (allowRemoveNavUnderLine) underLineFloating.current.style.cssText = `opacity: 0; left: ${left}`;
  }
  
  // for moving the underline for the floating nav
  function MoveFloatingBarUnderLineHover(index) {
    if (index == 0) {
      underLineFloating.current.style.cssText = "opacity: 1"
    } else if (index == 1) {
      underLineFloating.current.style.cssText = "left: 45px; opacity: 1"
    } else {
      underLineFloating.current.style.cssText = "left: 90px; opacity: 1"
    }
  }
  return (
    <>
    <div className="main-floating-content-container">
      
          < FloatingNaveRankContainer />
    </div>
    <div ref={floatingNavOverlay} className="floating-nav-overlay" onClick={() => removeFloatingNavOverlay(true)}></div>
    <div className="main-container">
      <div className="floating-container" onMouseLeave={() => MoveFloatingBarUnderLineLeave()} onClick={() => removeFloatingNavOverlay(false)}>
        <div ref={underLineFloating} className="floating-bar-under-hover"><span></span></div>
        <div onClick={() => showFloatingNavContent("noti")} onMouseOver={() => MoveFloatingBarUnderLineHover(0)} className="floating-icon">
          <i class="fa-solid fa-bell"></i>
        </div>
        <div onMouseOver={() => MoveFloatingBarUnderLineHover(1)} className="floating-rank-img">
          <img src={rankImg} alt="Rank" />
        </div>
        <div onMouseOver={() => MoveFloatingBarUnderLineHover(2)} className="floating-icon">
        <i class="fa-solid fa-user-group"></i>
        </div>
      </div>
    </div>
      <div className="play-status-container">
        
      </div>
    </>
  );
}
