import { useState } from "react";
import "../css/index.css";
import "../../../css/all.css";
export default function SideBar() {
    let [expand, setExpand] = useState(true);
    let [iconsWidthState, setIconsWidthState] = useState("40px");
    let [iconTitleOpacity, setIconTitleOpacity] = useState("0");
    let [iconTitleDisplay, setIconTitleDisplay] = useState("none");
    function expandSideBar(bool,e) {
        if (bool) {
            console.log("expanded");
            e.currentTarget.children[0].style.cssText = "transform: rotate(180deg)";
            e.currentTarget.parentElement.style.cssText = "width: 200px";
            setTimeout(() => {
                setIconsWidthState("100%");
                setTimeout(() => {
                    setIconTitleOpacity("1");
                    setIconTitleDisplay("block");
                },200)
            },200)
        } else {
            setIconsWidthState("40px")
            console.log("decreased")
            e.currentTarget.children[0].style.cssText = "transform: rotate(360deg)"
            e.currentTarget.parentElement.style.cssText = ""

        }
    }
   return (
    <div>
        <div className="main-side-bar">
            <div className="expand-side-bar" onClick={(e) => {expandSideBar(expand,e); expand ? setExpand(false) : setExpand(true)}} >
                <div><i class="fa-solid fa-arrow-right"></i></div>
            </div>
            <div className="icons-container" >
                <div className="icons-section" style={{width: iconsWidthState}}>
                    <div className="sidebar-icon icon-active"><i class="fa-solid fa-house"></i><span style={{opacity: iconTitleOpacity, display: iconTitleDisplay}}>Home</span></div>
                    <div className="sidebar-icon"><i class="fa-solid fa-gamepad"></i><span style={{opacity: iconTitleOpacity, display: iconTitleDisplay}}>Play</span></div>
                    <div className="sidebar-icon"><i class="fa-solid fa-arrow-up-short-wide"></i><span style={{opacity: iconTitleOpacity, display: iconTitleDisplay}}>Ranks</span></div>
                    <div className="sidebar-icon"><i class="fa-solid fa-rocket"></i><span style={{opacity: iconTitleOpacity, display: iconTitleDisplay}}>Nitro</span></div>
                    <div className="sidebar-icon"><i class="fa-solid fa-ellipsis"></i><span style={{opacity: iconTitleOpacity, display: iconTitleDisplay}}>More</span></div>
                </div>
                <div className="icons-section" style={{width: iconsWidthState}}>
                    <div className="sidebar-icon icon-settings"><i class="fa-solid fa-gear"></i><span style={{opacity: iconTitleOpacity, display: iconTitleDisplay}}>Settings</span></div>
                    </div>
            </div>
        </div>
    </div>
   )
}
