import { useState, useRef, useEffect, use } from "react";
import "./css/App.css";
import "./css/all.css";
function App() {
  const checkboxRef = useRef(null);
  const errBox = useRef(null);
  let [usernameState, setUsernameState] = useState("");
  let [passwordState, setPasswordState] = useState("");
  let [errVal, setErrVal] = useState("");
  let emailValidation = /^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}$/;
  // checks username length
  useEffect(() => {
    if (usernameState.length > 30) setUsernameState(usernameState.slice(0, -1));
  }, [usernameState]);
  // checks password length
  useEffect(() => {
    if (passwordState.length > 40) setPasswordState(passwordState.slice(0, -1));
  }, [passwordState]);
  // inputs onfocus animation
  function inputFocusFunc(e) {
    if (e.target.value.length <= 0)
      e.target.nextElementSibling.classList.add("placeholder-trans");
  }
  // inputs onblur animation
  function inputBlurFunc(e) {
    if (e.target.value.length <= 0)
      e.target.nextElementSibling.classList.remove("placeholder-trans");
  }
  // check box onclick function transition
  function overlayClicked(e) {
    // makes submit button go full width
    e.target.parentElement.style.cssText = "width: 100%;";
    // changes the background color
    e.target.parentElement.classList.add("checkbox-trans-color");
    // this checks bec when second clicking there is no nexSibling
    if (e.target.nextElementSibling)
      e.target.nextElementSibling.classList.add("icon-down-trans");
    // changes background color of the sign to transparent
    e.target.addEventListener("mouseleave", (e) =>
      e.target.parentElement.classList.add("checkbox-trans")
    );
    if (e.target.nextElementSibling)
      setTimeout(e.target.nextElementSibling.remove(), 300);
    // this is the letters up trans when onclick
    setTimeout(() => {
      let spans = e.target.previousElementSibling.children;
      for (let i = 0; i < spans.length; i++) {
        setTimeout(
          () =>
            (spans[i].style.cssText =
              "transform: translateY(0%) !important; color: white"),
          i * 50
        );
      }
    }, 300);
    // adjusts the width height and trans of the button
    setTimeout(() => {
      e.target.parentElement.style.cssText = "width: 80px; height: 36px;";
      e.target.parentElement.classList.add("hover-bg-white");
    }, 450);
    // makes the color of the text inside the button to convert to var(--main-color) when mouse over
    checkboxRef.current.onmouseover = () => {
      let spans = checkboxRef.current.children[0].children;
      for (let i = 0; i < spans.length; i++) {
        setTimeout(
          () =>
            (spans[i].style.cssText =
              "transform: translateY(0%) !important; color: var(--main-color)"),
          i * 50
        );
      }
    };

    // makes the color of the text go to white onleave
    checkboxRef.current.onmouseleave = () => {
      let spans = checkboxRef.current.children[0].children;
      for (let i = 0; i < spans.length; i++) {
        setTimeout(
          () =>
            (spans[i].style.cssText =
              "transform: translateY(0%) !important; color:white"),
          i * 50
        );
      }
    };

    let style = "text-shadow: 0 0 20px white; opacity: 0;";
    e.target.parentElement.nextElementSibling.style.cssText = style;
    setTimeout(() => {
      e.target.parentElement.nextElementSibling.style.cssText =
        style + "font-size: 0;";
      e.target.parentElement.parentElement.style.cssText = "margin-bottom: 0;";
    }, 400);
    // remove the overlay cover so when hovering we are going to be hovering on the main element which is div.checkbox
    setTimeout(() => e.target.remove(), 600);
  }
  // starts when there is an issue with the inputs
  function showError(text) {
    errBox.current.textContent = text;
    errBox.current.style.cssText = "font-size: 18px;";
    setTimeout(() => (errBox.current.style.cssText = ""), 2000);
  }
  // username length checking
  function usernameBlur(e) {
    if (e.target.value.trim().length < 1) showError("username is required");
    else if (e.target.value.trim().length <= 3)
      showError("Username must be more than 3 characters");
  }
  // email input checking on blur
  function emailCheck(e) {
    if (e.target.value.trim().length < 1) showError("Email is required");
    else if (!e.target.value.trim().match(emailValidation))
      showError("email not valid");
  }
  // password length checking
  function passBlur(e) {
    if (e.target.value.trim().length < 1) showError("Password is required");
    else if (e.target.value.trim().length <= 8)
      showError("Password must be more than 8 characters");
  }
  return (
    <div className="container">
      <div className="sign-container">
        <div ref={errBox} className="err-popup"></div>
        <div className="sign-up">
          <div className="title">Sign up</div>
          <div className="information">
            <div className="input-container">
              <input
                onBlur={(e) => {
                  inputBlurFunc(e);
                  usernameBlur(e);
                }}
                onFocus={(e) => inputFocusFunc(e)}
                value={usernameState}
                onInput={(e) => setUsernameState(e.target.value)}
              />
              <div
                onClick={(e) => e.target.previousElementSibling.focus()}
                className="placeholder"
              >
                username
              </div>
            </div>
            <div className="input-container">
              <input
                onBlur={(e) => {
                  inputBlurFunc(e);
                  emailCheck(e);
                }}
                onFocus={(e) => inputFocusFunc(e)}
              />
              <div
                onClick={(e) => e.target.previousElementSibling.focus()}
                className="placeholder"
              >
                email
              </div>
            </div>
            <div className="input-container">
              <input
                onBlur={(e) => {
                  inputBlurFunc(e);
                  passBlur(e);
                }}
                onFocus={(e) => inputFocusFunc(e)}
                value={passwordState}
                onInput={(e) => setPasswordState(e.target.value)}
              />
              <div
                onClick={(e) => e.target.previousElementSibling.focus()}
                className="placeholder"
              >
                password
              </div>
              <div className="eyeIcon">
                <i class="fa-solid fa-hurricane"></i>
              </div>
            </div>
          </div>
          <div className="agreements">
            <div ref={checkboxRef} className="checkbox">
              <div className="sign-up-text-checkbox">
                <span>s</span>
                <span>i</span>
                <span>g</span>
                <span>n</span>
                <span>u</span>
                <span>p</span>
              </div>
              <div
                onClick={(e) => overlayClicked(e)}
                onMouseLeave={(e) =>
                  e.target.parentElement.classList.remove("checkbox-trans")
                }
                onMouseOver={(e) =>
                  e.target.parentElement.classList.add("checkbox-trans")
                }
                className="overlay-checkbox"
              ></div>
              <i class="fa-solid fa-check check-icon "></i>
            </div>
            <div className="checkbox-info">
              <span>I have read and accepted all terms and conditions </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
