import React, { useState } from 'react';

function App() {
  const [name, setName] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send data to the PHP backend
      const result = await fetch('http://localhost/react_proj/api.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name }), // Send data as JSON
      });

      console.log(name, JSON.stringify({name}))

      // Parse the JSON response
      const data = await result.json();
      setResponse(data.message); // Update state with the response
    } catch (error) {
      console.error('Error:', error);
      setResponse('An error occurred while sending data.');
    }
  };

  return (
    <div>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your name"
          required
        />
        <button type="submit" onClick={handleSubmit}>Submit</button>
      <p>Response: {response}</p>
    </div>
  );
}

export default App;
