useEffect(() => {
  const trailCount = 10;
  const trailElements = [];

  for (let i = 0; i < trailCount; i++) {
    const trailDiv = document.createElement("div");
    trailDiv.className = "cursor-trail-effect";
    websiteMainContainer.current.appendChild(trailDiv);
    trailElements.push(trailDiv);
  }

  let currentTrail = 0;

  window.onmousemove = (e) => {
    const y = e.pageY;
    const x = e.pageX;
    mainCursorContainer.current.style.cssText = `top: ${y - 5}px; left: ${x - 5}px`;

    const trailDiv = trailElements[currentTrail];
    trailDiv.style.cssText = `top: ${y - 7}px; left: ${x - 7}px; opacity: 1; transition: opacity 0.5s`;

    setTimeout(() => {
      trailDiv.style.opacity = 0;
    }, 0);

    currentTrail = (currentTrail + 1) % trailCount;
  };

  return () => {
    window.onmousemove = null;
  };
}, []);
