import { BrowserRouter, Routes, Route } from "react-router-dom";
import Sign from "./components/sign_comp/js/sign.js";
import Err from "./components/err_comp/js/index.js";
function App() {
  async function signUser(username, password) {
    try {
      const fet = await fetch("http://localhost/react_proj/sign_in_api.php", {
        method: "POST",
        headers: { "content-type": "application/json" },
        body: JSON.stringify({ username: username, password: password }),
      });
      const res = await fet.json();
      if (res.message == "valid login") return true;
    } catch (e) {
      return false
    }
  }

  function returnSign() {
    if (signUser( window.localStorage["username"], window.localStorage["password"])) {
      return <Route path="/" element={<Sign />} />;
    } else {
      return <Route path="/" element={<Sign />} />;
    }
  }
  return (
    <>
      {
        <BrowserRouter>
          <Routes>
            {returnSign()}
            <Route path="*" element={<Err />} />
          </Routes>
        </BrowserRouter>
      }
    </>
  );
}

export default App;
