import "../css/topNav.css";
import VerantsElement from "../../verants_comp/js/verants.js";
import { useRef, useEffect } from "react";
function TopNav() {

  const topNavMainContainer = useRef(null);

  useEffect(() => {
    // topNavMainContainer.current.children.forEach((res) => {
    //   console.log(res)
    // })
    Array.from(topNavMainContainer.current.children).forEach((el) => {
      
      el.onmouseover = () => {
        overAudio.play();
        el.style.cssText = `color: black; background-color: white`;
      };

      el.onmouseleave = () => {
        el.style.cssText = `transition: 0.3s linear; `;
      };

      el.onclick = () => new Audio("/sounds/default_nav_click.mp3").play()
    })
  },[])

  return (
    <>
      
        <div className="top-home-nav-bar">
          <div ref={topNavMainContainer} className="top-nav-home-options">
            <div className="top-nav-option top-nav-active-option">Home</div>
            <div className="top-nav-option">Shop</div>
            <div className="top-nav-option v-pass-top-nav-text">V-Pass</div>
            <div className="top-nav-option">Skin</div>
            <div className="top-nav-option">Rank</div>
            <div className="top-nav-option">Verants</div>
          </div>
          <div className="verants-container">
            <div className="verants-all-container">
              <div className="home-element-verants-symbol">
                <VerantsElement scale="0.34" />
              </div>
              <div className="verants-count">1,500</div>
            </div>
          </div>
          <div className="more-options-bars-icon">
            <i class="fa-solid fa-bars"></i>
          </div>
        </div>
      
    </>
  );
}

export default TopNav;
