import { useRef } from "react";
import "../css/index.css";
export default function FloatingeBar() {
  const underLineFloating = useRef();
  const floatingNavOverlay = useRef();
  // this for making the underline that is under the floating icon stay when hovering over the blur overlay
  function keepFloatingNavOverlay(bool) {

  }
  // this func for removing the blur overlay
  function removeFloatingNavOverlay(bool) {
    if (bool) {
      floatingNavOverlay.current.style.cssText = "opacity: 0";
      setTimeout(() => floatingNavOverlay.current.style.cssText = "opacity: 0, display: none",200)
    } else {
      floatingNavOverlay.current.style.cssText = "opacity: 0";
      
      setTimeout(() => floatingNavOverlay.current.style.cssText = "opacity: 1",200)
    }
  }
  // for moving the underline for thefloating nav
  function MoveFloatingBarUnderLineLeave() {
    const left = underLineFloating.current.style.left;
    underLineFloating.current.style.cssText = `opacity: 0; left: ${left}`;
  }
  // for moving the underline for the floating nav
  function MoveFloatingBarUnderLineHover(index) {
    if (index == 0) {
      underLineFloating.current.style.cssText = "opacity: 1"
    } else if (index == 1) {
      underLineFloating.current.style.cssText = "left: 45px; opacity: 1"
    } else {
      underLineFloating.current.style.cssText = "left: 90px; opacity: 1"
    }
  }
  return (
    <>
    <div ref={floatingNavOverlay} className="floating-nav-overlay" onMouseOver={() => keepFloatingNavOverlay()}></div>
    <div className="main-container">
      <div className="floating-container" onMouseLeave={() => MoveFloatingBarUnderLineLeave()}>
        <div ref={underLineFloating} className="floating-bar-under-hover"><span></span></div>
        <div onMouseOver={() => MoveFloatingBarUnderLineHover(0)} className="floating-icon">
          <i class="fa-solid fa-bell"></i>
        </div>
        <div onMouseOver={() => MoveFloatingBarUnderLineHover(1)} className="floating-icon">
          <i class="fa-solid fa-gem"></i>
        </div>
        <div onMouseOver={() => MoveFloatingBarUnderLineHover(2)} className="floating-icon">
        <i class="fa-solid fa-user-group"></i>
        </div>
      </div>
      <div></div>
    </div>
    </>
  );
}
