import "../css/nav.css";
import { useRef } from "react";
import { useNavigate } from 'react-router-dom';

export default function BottomNav(props) {
  const nav = useNavigate();
  const firstElement = useRef(null);
  const secondElement = useRef(null);
  const thirdElement = useRef(null);
  const fourthElement = useRef(null);

  if (props.show) {
    setTimeout(() => (firstElement.current.style.cssText = "height: 200px"), 0);
    setTimeout(
      () => (secondElement.current.style.cssText = "height: 100%"),
      100
    );
    setTimeout(() => {
      thirdElement.current.style.cssText = "height: 100%";
      setTimeout(
        () =>
          {firstElement.current.style.cssText =
            "height: 200px; border-top: 1px solid var(--secondary-color); border-top-right-radius: 10px; border-top-left-radius: 10px;";
            fourthElement.current.style.cssText = "opacity: 1;";
          },
        500
      );
    }, 200);

    return (
      <div ref={firstElement} className={`bottom-nav-main-container`}>
        <div ref={secondElement} className="second-trasition-bottom-nav"></div>
        <div ref={thirdElement} className="third-transition-bottom-nav"></div>
        <div ref={fourthElement} className="bottom-content-bottom-nav-main-container">
          <div onClick={() => nav("/")} className="bottom-nav-navigation-content bottom-nav-navigation-content-active">
            Home
          </div>
          <div onClick={() => nav("/longs")} className="bottom-nav-navigation-content">Longs</div>
          <div onClick={() => nav("/shorts")} className="bottom-nav-navigation-content">Shorts</div>
          <div className="bottom-nav-navigation-content-me">Contact Me</div>
        </div>
      </div>
    );
  } 
}
