export default function addCommaToNum(number) {
    const gotNum = String(number).split(""); 
    let num = [];
    let count = 1;
    if (number.length > 3) {
        for (let i = (gotNum.length - 1); i <= 0; i--) {
            if (count === 3) {
                count = 1
            } else {
                num.unshift
            }
            count++
        }
    }
    console.log(num)
}
