function App() {

    let [input, setInput] = useState('');
    let [values, setValues] = useState(["Hi"]);

    async function putNget() {
        setValues([...values, "hello"])
        try {
            await fetch("http://localhost/react_proj/api.php", {
                method: "POST",
                headers: {"Content-type": "application/json"},
                body: JSON.stringify({"new_input": input})
            })
        } catch(e) {
            console.error(e);
        }
    }

return (
    <>
        <input onChange={(e) => setInput(e.target.value)} />
        <button onClick={putNget} >add</button>
        <ul>
            {values.forEach((e) => {
                return <li>{e}</li>
            })}
        </ul>
    </>
)
}

export default App;
